mapper.insert:
	INSERT ALL
    INTO ANNOUNCEMENT 
    (
    ANNOUNCEMENT_NO
    ,MANAGER_NO
    ,TITLE,CONTENT 
    ) VALUES 
    (
    SEQ_ANNOUNCEMENT_NO.NEXTVAL
    ,#{managerNo}
    ,#{title}
    ,#{content}
    )  

    INTO ANNOUNCEMENT_IMG 
    (
    IMG_NO
    ,ANNOUNCEMENT_NO
    ,IMG_NAME
    ,PATH
    ,ORIGIN_NAME
    ) 
    VALUES 
    (
    (SELECT GET_ITEM_SEQ() FROM DUAL)
    ,SEQ_ANNOUNCEMENT_NO.CURRVAL
    ,#{file.imgName}
    ,#{file.path}
    ,#{file.originName}
    )

    SELECT * FROM DUAL

mapper.update:
UPDATE 
ANNOUNCEMENT 
SET 
MODIFY_DATE = SYSDATE 

,TITLE = #{title}


,CONTENT = #{content}

WHERE  ANNOUNCEMENT_NO= #{announcementNo}
AND DEL_YN='N'

