mapper.select:
SELECT COUNT(REPLY_NO)
	FROM BOARD_REPLY
	WHERE BOARD_NO = #{boardNo}

SELECT COUNT(MEMBER_NO)
	FROM BOARD_LIKE
	WHERE BOARD_NO = #{boardNo}

SELECT COUNT(MEMBER_NO)
	FROM BOARD_LIKE
	WHERE BOARD_NO = #{boardNo}
	AND MEMBER_NO = #{memberNo}

mapper.insert:
INSERT INTO BOARD_LIKE(
    MEMBER_NO
    , BOARD_NO
) VALUES (
    #{memberNo}
    , #{boardNo}
)

mapper.delete:
DELETE FROM BOARD_LIKE
	WHERE BOARD_NO = #{boardNo}
	AND MEMBER_NO = #{memberNo}

mapper.insert:
INSERT INTO CATEGORY(
	    CATEGORY_NO
	    , CATEGORY_NAME
	) VALUES (
	    SEQ_CATEGORY_NO.NEXTVAL
	    , #{categoryName}

INSERT INTO RESIDENT_BOARD(
	    BOARD_NO
	    , WRITER_NO
	    , CATEGORY_NO
	    , TITLE
	    , CONTENT
	) VALUES (
	    SEQ_RESIDENT_BOARD_NO.NEXTVAL
	    , #{writerNo}
	    , #{categoryNo}
	    , #{title}
	    , #{content}
	)

INSERT INTO RESIDENT_BOARD_IMG(
	    IMG_NO
	    , BOARD_NO
	    , IMG_NAME
	    , PATH
	    , ORIGIN_NAME
	) VALUES (
	    SEQ_RESIDENT_BOARD_IMG_NO.NEXTVAL
	    , #{boardNo}
	    , #{imgName}
	    , #{path}
	    , #{originName}
	)

mapper.update:
UPDATE RESIDENT_BOARD


		    CATEGORY_NO = #{categoryNo}


	    	TITLE = #{title}


	    	CONTENT = #{content}

	    MODIFY_DATE = SYSDATE				

	WHERE BOARD_NO = #{boardNo}

UPDATE RESIDENT_BOARD_IMG


		    IMG_NAME = #{imgName}


	    	PATH = #{path}


	    	ORIGIN_NAME = #{originName}

	    MODIFY_DATE = SYSDATE				

	WHERE BOARD_NO = #{boardNo}

mapper.insert:
INSERT INTO BOARD_REPLY(
    REPLY_NO
    , MEMBER_NO
    , BOARD_NO
    , CONTENT
)
VALUES (
    SEQ_BOARD_REPLY_NO.NEXTVAL
    , #{memberNo}
    , #{boardNo}
    , #{content}
)

mapper.update:
UPDATE BOARD_REPLY
	SET
	    CONTENT = #{content}
	    , MODIFY_DATE = SYSDATE
	WHERE REPLY_NO = #{replyNo}
