mapper.insert:
INSERT INTO FACILITIES(
    FACILITIES_NO
    , FACILITIES_NAME
    , UNIT_PRICE
    , LOCATION
    , OPERATION_TIME
    , DAY_OFF
    , CONTACT
    , AMENITY
    , IMAGE
) VALUES (
    SEQ_FACILITIES_NO.NEXTVAL
    , #{facilitiesName}
    , #{unitPrice}
    , #{location}
    , #{operationTime}
    , #{dayOff}
    , #{contact}
    , #{amenity}
    , #{image}
)

mapper.update:
UPDATE FACILITIES


			FACILITIES_NAME = #{facilitiesName}


			UNIT_PRICE = #{unitPrice}


			LOCATION = #{location}


			OPERATION_TIME = #{operationTime}


			DAY_OFF = #{dayOff}


			CONTACT = #{contact}


			AMENITY = #{amenity}


			IMAGE = #{image}

	    MODIFY_DATE = SYSDATE

	WHERE FACILITIES_NO = #{facilitiesNo}

UPDATE FACILITIES
	SET
	    DEL_YN = #{delYn}
	    , MODIFY_DATE = SYSDATE
	WHERE FACILITIES_NO = #{facilitiesNo}

mapper.insert:
INSERT INTO FACILITIES_HISTORY(
    APPLICATION_NO
    , FACILITIES_NO
    , MEMBER_NO
    , USE_DATE
    , PRICE
) VALUES (
    SEQ_FACILITIES_HISTORY_NO.NEXTVAL
    , #{facilitiesNo}
    , #{memberNo}
    , #{useDate}
    , #{price}
)

mapper.update:
UPDATE 
    FACILITIES_HISTORY
SET 
    CANCEL_DATE = SYSDATE
WHERE MEMBER_NO = #{memberNo}
	AND APPLICATION_NO = #{applicationNo}
