mapper.select:
		SELECT COUNT(*)
    	FROM MEMBER 
    	WHERE PHONE = #{phone}

mapper.insert:
INSERT INTO MEMBER 
(
MEMBER_NO
,UNIT_NO
,PHONE
,PWD
,NAME
,GENDER
,BIRTH
,OWNER_YN
) 
VALUES 
(
SEQ_MEMBER_NO.NEXTVAL
,#{unitNo}
,#{phone}
,#{pwd}
,#{name}
,#{gender}
,#{birth}
,#{ownerYn}
)

mapper.update:
UPDATE MEMBER
PWD = #{pwd}, NAME = #{name}, GENDER = #{gender}
WHERE MEMBER_NO = #{memberNo} AND DEL_YN ='N'

UPDATE MEMBER 
SET 
DEL_YN = 'Y' 
,PHONE =#{phone}
WHERE 
MEMBER_NO = #{memberNo}
