mapper.insert:
INSERT INTO 
	VOTE_BOARD
(
    VOTE_NO
    ,MANAGER_NO
    ,TITLE
    ,CONTENT
)
VALUES 
(
    SEQ_VOTE_BOARD_NO.NEXTVAL
    ,#{managerNo}
    ,#{title}
    ,#{content}
)

insert.foreach:
INTO

VOTE_ITEM
(
    ITEM_NO
    ,VOTE_NO
    ,ITEM_NAME
    ,VOTE_ORDER
    ,VOTE_TYPE
)
VALUES
(
    (SELECT GET_ITEM_SEQ() FROM DUAL )
    ,#{item.voteNo}
    ,#{item.itemName}
    ,#{item.voteOrder}
    ,#{item.voteType}
)

INTO
    VOTE_HISTORY
(
    VOTE_HISTORY_NO
    ,VOTE_NO
    ,VOTE_NAME
    ,VOTE_COUNT
    ,VOTE_ORDER
)
VALUES
(
    (SELECT GET_HISTORY_SEQ() FROM DUAL )
    ,#{item.voteNo}
    ,#{item.voteName}
    ,#{item.voteCount}
    ,#{item.voteOrder}
)

mapper.insert:
INSERT INTO
    VOTE_REPLY
(
    REPLY_NO
    ,VOTE_NO
    ,ITEM_NO
    ,PRTC_NO
)
VALUES
(
    SEQ_VOTE_REPLY_NO.NEXTVAL
    ,#{voteNo}
    ,#{itemNo}
    ,#{prtcNo}
)

mapper.select:
SELECT VOTE_NO ,V.MANAGER_NO ,TITLE ,CONTENT ,ENROLL_DATE ,MODIFY_DATE ,DEADLINE_DATE ,ACCEPT_YN ,ID FROM VOTE_BOARD V JOIN MANAGER M ON M.MANAGER_NO = V.MANAGER_NO
AND ID = #{managerId}
AND TITLE like '%${title}%'
AND CONTENT like '%${content}%'
AND (ENROLL_DATE BETWEEN TO_DATE(#{enrollDateStart}) AND TO_DATE(#{enrollDateEnd}))
AND DEADLINE_DATE IS NULL
AND (DEADLINE_DATE BETWEEN TO_DATE(#{deadlineDateStart}) AND TO_DATE(#{deadlineDateEnd}))
AND DEL_YN = #{delYn}
AND ACCEPT_YN = #{acceptYn}
ORDER BY VOTE_NO DESC
